use std::str::CharRange;

fn part1 (input: &'static str) -> String  {
  let mut i:usize = 0;
  let mut total:i32 = 0;
  while i < input.len() {
      let CharRange {ch, next} = input.char_range_at(i);
      if ch == '(' {
        total += 1;
      } else if ch == ')' {
        total -= 1;
      }
      i = next;
  }

  return total.to_string();
}

fn part2 (input: &'static str) -> String  {
  let mut i:usize = 0;
  let mut total:i32 = 0;
  while i < input.len() {
      let CharRange {ch, next} = input.char_range_at(i);
      if ch == '(' {
        total += 1;
      } else if ch == ')' {
        total -= 1;
      }

      i = next;

      if total < 0 {
        return i.to_string();
      }
  }

  return i.to_string();
}

